mapper.select:
SELECT count(empl_no) FROM employee WHERE 1=1
AND empl_no LIKE '%' || #{emplNo} || '%'
AND empl_nm LIKE '%' || #{emplNm} || '%'
AND empl_position = #{emplPosition}
AND dept_code = #{deptCode}

select * from ( select a.* , ROW_NUMBER() OVER (ORDER BY a.empl_creatdt DESC) rnum FROM ( SELECT e.empl_no ,empl_pwd , empl_nm , c.cc_name as empl_position , d.dept_name as dept_code , t.team_name as team_code , empl_signature , empl_brthdy , hire_date , retire_date , retire_resn , enable , profile_imgpath , basic_salary , bank_code , bank_name , account , zip_code , addr_main , addr_detail , empl_telno , empl_gender , empl_email , empl_hobby , empl_mrnry , householder_at , vehicle_number , empl_creatdt FROM employee e LEFT JOIN common_code c ON e.EMPL_POSITION = c.cc_code AND c.cg_group = 'POSITION' LEFT JOIN DEPARTMENT d ON e.dept_code = d.dept_code LEFT JOIN TEAM t ON e.team_code = t.team_code WHERE 1=1
AND e.empl_no LIKE '%' || #{emplVO.emplNo} || '%'
AND empl_nm LIKE '%' || #{emplVO.emplNm} || '%'
AND e.empl_position = #{emplVO.emplPosition}
AND e.dept_code = #{emplVO.deptCode}
ORDER BY empl_creatdt DESC
