mapper.insert:

  			SELECT notice_seq.nextval FROM dual

  		INSERT INTO notice(
  			 notice_no
			,notice_writer
			,notice_title
			,notice_content
			,notice_date
			,notice_hit
			,file_identify
  		)VALUES(
  			 #{noticeNo}
  			,#{noticeWriter}
  			,#{noticeTitle}
  			,#{noticeContent}
  			,sysdate
  			,0
  			,#{fileIdentify}
  		)

mapper.select:
SELECT b.* FROM( SELECT a.*, row_number() over (order by a.notice_no desc)rnum FROM( SELECT notice_no ,notice_writer ,notice_title ,notice_content ,notice_date ,notice_udate ,notice_hit ,file_identify FROM notice
ORDER BY notice_no desc )a )b
WHERE b.rnum >= #{startRow} and b.rnum <= #{endRow}

mapper.update:
UPDATE notice
SET notice_hit = notice_hit + 1
WHERE notice_no = #{noticeNo}

UPDATE notice
SET
	 notice_title = #{noticeTitle}
	,notice_content = #{noticeContent}
	,notice_udate = sysdate
WHERE notice_no = #{noticeNo}
