mapper.insert:
INSERT INTO  doc_file (
	  file_identify
	, file_no
	, file_name
	, file_size
	, file_fancysize
	, file_mime
	, file_path
	, file_regdate
) 
VALUES (
	#{fileIdentify}
	, (
		SELECT NVL(MAX(file_no), 0) + 1 
		FROM doc_file 
		WHERE file_identify = #{fileIdentify}
	  )
	, #{fileName}
	, #{fileSize}
	, #{fileFancysize}
	, #{fileMime}
	, #{filePath}
	, sysdate
)

mapper.select:
SELECT * 
FROM doc_file
WHERE file_identify = #{fileIdentify}
AND file_no = #{fileNo}
