mapper.insert:
	INSERT INTO book (
		  BOOK_NO
		, BOOK_NAME 
		, BOOK_PRICE 
		, BOOK_COMPANY 
	)
    VALUES (
    	  #{bookNo}
    	, #{bookName}
    	, #{bookPrice}
    	, #{bookCompany}
    )

mapper.select:
select * from ( select EDC_NO ,EDC_SORT ,EDC_WAY ,EDUCATOR ,EDC_TARGET ,EDC_GRADE ,EDC_PSNCPA ,EDC_TITLE ,EDC_CONTENT ,RECRUIT_SDATE ,RECRUIT_EDATE ,EDC_SDATE ,EDC_EDATE ,EDC_ACTIVE ,ROW_NUMBER() OVER (ORDER BY a.edc_no DESC) rnum from ( SELECT * FROM education WHERE 1=1
) a ) b
WHERE b.rnum >= #{page.startRow} AND b.rnum <= #{page.endRow}

SELECT * FROM ( SELECT e.EDC_NO AS edcNo, e.EDC_SORT AS edcSort, e.EDUCATOR AS educator, e.EDC_TARGET AS edcTarget, e.EDC_GRADE AS edcGrade, e.EDC_TITLE AS edcTitle, e.EDC_CONTENT AS edcContent, e.EDC_SDATE AS edcSdate, e.EDC_EDATE AS edcEdate, e.EDC_ACTIVE AS edcActive, NVL(ee.EE_STATUS, '미신청') AS eeStatus, NVL(TO_CHAR(ee.EE_CDATE, 'YYYY-MM-DD HH24:MI:SS'), '-') AS eeCdate, ROW_NUMBER() OVER (ORDER BY e.EDC_NO DESC) rnum FROM education e LEFT JOIN emp_edc ee ON e.EDC_NO = ee.EDC_NO WHERE ee.EMPL_NO = '202501002'
) a
WHERE a.rnum >= #{page.startRow} AND a.rnum <= #{page.endRow}
