mapper.update:
UPDATE cr_participant 
SET last_read_cm_no = #{cmNo}
WHERE cr_no = #{crNo}
  AND empl_no = #{emplNo}

mapper.select:
SELECT COALESCE(last_read_cm_no, 0)
FROM cr_participant
WHERE cr_no = #{crNo}
  AND empl_no = #{emplNo}

SELECT COUNT(*)
FROM chat_msg cm
JOIN cr_participant cp ON cm.cr_no = cp.cr_no
WHERE cm.cr_no = #{crNo}
  AND cp.empl_no = #{emplNo}
  AND cm.cm_no > COALESCE(cp.last_read_cm_no, 0)
  AND cm.empl_no != #{emplNo}  
  AND cp.cm_exit = 'N'

SELECT MAX(cm_date)
FROM chat_msg
WHERE cr_no = #{crNo}

SELECT COUNT(*)
FROM cr_participant
WHERE cr_no = #{crNo}
  AND (last_read_cm_no < #{cmNo} OR last_read_cm_no IS NULL)
  AND empl_no != #{emplNo}

mapper.update:
UPDATE cr_participant
SET cm_exit = 'Y'
WHERE cr_no = #{crNo}
  AND empl_no = #{emplNo}
RETURNING cr_no INTO #{crNo}

mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM cr_participant
WHERE cr_no = #{crNo}
  AND empl_no = #{emplNo}
  AND cm_auth = 'HOST'

mapper.update:
UPDATE cr_participant
SET cm_auth = 'HOST'
WHERE cr_no = #{crNo}
  AND empl_no = #{emplNo}

mapper.select:
SELECT empl_no
FROM chat_msg
WHERE cm_no = #{cmNo}

SELECT empl_no
 FROM cr_participant
 WHERE cr_no = #{crNo}
