mapper.select:
SELECT ps_no , empl_no , total_payment , total_deduction , actual_salary , basic_salary , overtime_pay , ps_bonus , meal_pay , income_tax , local_tax , employment_insurance , health_insurance , nursing_insurance , national_pension , pay_date , pay_at FROM pay_stub WHERE empl_no = #{emplNo} AND pay_at = 'Y'
AND TO_CHAR(pay_date, 'YYYY') = #{yearr}
AND TO_CHAR(pay_date, 'MM') = #{monthh}
AND TO_CHAR(pay_date, 'DD') = #{dayy}
ORDER BY pay_date DESC

mapper.delete:
DELETE FROM pay_stub
WHERE empl_no = #{emplNo}
AND pay_at = 'N'
