mapper.select:
SELECT app_no , rec_no , app_nm , app_date , app_telno , app_email , app_at , app_comment , app_value FROM ( SELECT app_no , rec_no , app_nm , app_date , app_telno , app_email , app_at , app_comment , app_value , rownum AS rnum FROM ( SELECT app_no , rec_no , app_nm , app_date , app_telno , app_email , app_at , app_comment , app_value FROM applier WHERE rec_no = #{recNo}
AND ( app_nm LIKE '%' || #{searchWord} || '%' OR (#{searchWord} = '채용' AND app_at = 'Y') OR (#{searchWord} = '미채용' AND app_at = 'N') )
ORDER BY app_date DESC ) WHERE rownum <= #{endRow} ) WHERE rnum >= #{startRow}

mapper.update:
UPDATE applier 
SET app_at = 'Y' 
WHERE rec_no = #{recNo} 
AND app_nm = #{appNm}

mapper.select:
SELECT COUNT(*) FROM ( SELECT rec_no , CASE dept_code WHEN '01' THEN '경영 및 인사' WHEN '02' THEN '마케팅' WHEN '03' THEN '연구 및 개발' WHEN '04' THEN '운영' WHEN '05' THEN '재무 및 회계' ELSE '기타' END AS deptName , rec_title , CASE rec_position WHEN '01' THEN '사원' WHEN '02' THEN '대리' WHEN '03' THEN '과장' WHEN '04' THEN '차장' WHEN '05' THEN '부장' ELSE '기타' END AS positionName FROM recruitment WHERE 1=1
AND ( CASE dept_code WHEN '01' THEN '경영 및 인사' WHEN '02' THEN '마케팅' WHEN '03' THEN '연구 및 개발' WHEN '04' THEN '운영' WHEN '05' THEN '재무 및 회계' ELSE '기타' END LIKE '%' || #{searchWord} || '%' OR rec_title LIKE '%' || #{searchWord} || '%' OR CASE rec_position WHEN '01' THEN '사원' WHEN '02' THEN '대리' WHEN '03' THEN '과장' WHEN '04' THEN '차장' WHEN '05' THEN '부장' ELSE '기타' END LIKE '%' || #{searchWord} || '%' )
)

SELECT * FROM ( SELECT a.*, rownum rnum FROM ( SELECT rec_no, dept_code, CASE dept_code WHEN '01' THEN '경영 및 인사' WHEN '02' THEN '마케팅' WHEN '03' THEN '연구 및 개발' WHEN '04' THEN '운영' WHEN '05' THEN '재무 및 회계' ELSE '기타' END AS deptName, rec_title, rec_position, CASE rec_position WHEN '01' THEN '사원' WHEN '02' THEN '대리' WHEN '03' THEN '과장' WHEN '04' THEN '차장' WHEN '05' THEN '부장' ELSE '기타' END AS positionName, rec_content, rec_req, rec_ideal, rec_sdate, rec_edate, rec_exp, rec_target, rec_treatment, rec_essential, CASE rec_usage WHEN 'Y' THEN '진행중' WHEN 'N' THEN '완료' ELSE '알 수 없음' END AS usageStatus, rec_usage AS recUsage FROM recruitment WHERE 1=1
AND rec_usage = #{status}
AND ( CASE dept_code WHEN '01' THEN '경영 및 인사' WHEN '02' THEN '마케팅' WHEN '03' THEN '연구 및 개발' WHEN '04' THEN '운영' WHEN '05' THEN '재무 및 회계' ELSE '기타' END LIKE '%' || #{searchWord} || '%' OR rec_title LIKE '%' || #{searchWord} || '%' OR CASE rec_position WHEN '01' THEN '사원' WHEN '02' THEN '대리' WHEN '03' THEN '과장' WHEN '04' THEN '차장' WHEN '05' THEN '부장' ELSE '기타' END LIKE '%' || #{searchWord} || '%' )
ORDER BY rec_sdate DESC ) a WHERE rownum <= #{endRow} ) WHERE rnum >= #{startRow}

mapper.insert:

    SELECT 'REC' || TO_CHAR(recruitment_seq.NEXTVAL, 'FM00000') FROM DUAL


INSERT INTO recruitment (
    rec_no
    , dept_code
    , rec_title
    , rec_position
    , rec_content
    , rec_req
    , rec_ideal
    , rec_sdate
    , rec_edate
    , rec_exp
    , rec_target
    , rec_treatment
    , rec_essential
    , rec_usage
) VALUES (
    #{recNo}
    , #{deptCode}
    , #{recTitle}
    , #{recPosition}
    , #{recContent}
    , #{recReq}
    , #{recIdeal}
    , #{recSdate}
    , #{recEdate}
    , #{recExp}
    , #{recTarget}
    , #{recTreatment}
    , #{recEssential}
    , 'Y'
)

INSERT INTO rec_sort (
      sort_no
    , rec_no
    , sort_content
) VALUES (
      'S' || TO_CHAR(recruitment_seq.NEXTVAL, 'FM00000')
    , #{recNo}
    , #{sortContent}
)

mapper.update:
UPDATE recruitment
SET rec_usage = 'N'
WHERE rec_no = #{recNo}

UPDATE APPLIER
SET APP_COMMENT = #{appComment},
    APP_VALUE = #{appValue}
WHERE APP_NO = #{appNo}
