mapper.update:
update sanctioner
set
	sanctn_status = '02'
where doc_no = #{docNo}
and sanctner_no = #{userId}

update sanctioner
set
	sanctn_status = '03',
	sanctn_date = sysdate
where doc_no = #{docNo}
and sanctner_no = #{userId}

mapper.select:
